pragma solidity ^0.8.24;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IPriceToken.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IStableSwapLP.sol";
import "./interfaces/IPoolFactory.sol";

contract VaultTestnet is Ownable {
    struct Tier {
        uint256 apr;
        uint256 locktime;
        uint256 minUSDStake;
    }

    struct UserInvestment {
        address pool;
        uint256 startTime;
        uint256 endTime;
        uint256 tier;
        address[] tokens;
        uint256[] amounts;
        address lp;
    }

    mapping(address => mapping(uint256 => UserInvestment))
        public userInvestmentList;

    mapping(address => uint256) public userCurrentIds;

    mapping(address => mapping(uint256 => uint256)) public avaliableUserRewards;

    mapping(address => mapping(uint256 => uint256)) public lockedUserRewards;

    mapping(address => mapping(uint256 => uint256)) public userLockedTokens;

    mapping(uint256 => Tier) public tiers;

    uint256 public numberUser;
    mapping(address => uint256) public userToNum;
    mapping(uint256 => address) public numToUser;

    address public priceToken;

    uint256 public lastTimeUpdate;

    address public thorn;

    address public poolFactory;

    constructor(
        address _priceToken,
        address _thorn,
        address _poolFactory
    ) Ownable(msg.sender) {
        priceToken = _priceToken;
        thorn = _thorn;
        poolFactory = _poolFactory;

        tiers[0] = Tier({apr: 25, locktime: 60 * 5, minUSDStake: 50 * 1e6});
        tiers[1] = Tier({apr: 25, locktime: 60 * 10, minUSDStake: 100 * 1e6});
        tiers[2] = Tier({apr: 25, locktime: 60 * 15, minUSDStake: 500 * 1e6});
        tiers[3] = Tier({apr: 25, locktime: 60 * 20, minUSDStake: 1000 * 1e6});

        lastTimeUpdate = block.timestamp;
    }

    function stake(
        address pool,
        address[] memory tokens,
        uint256[] memory amounts,
        uint256 tier
    ) public {
        require(tier < 4, "LV: Invalid tier");
        // check
        uint256 totalUSD = checkPoolAndGetTotalUSDT(pool, tokens, amounts);
        require(totalUSD > tiers[tier].minUSDStake, "LV: Min USD stake");
        // transfer token to user to vault
        for (uint i = 0; i < tokens.length; i++) {
            withdrawFromUser(tokens[i], msg.sender, amounts[i]);
        }
        // create user investment
        addToAddressList(msg.sender);
        userCurrentIds[msg.sender] += 1;
        uint256 id = userCurrentIds[msg.sender];
        // transfer token from vault to pool
        userLockedTokens[msg.sender][id] = depositVaultToPool(
            pool,
            tokens,
            amounts
        );
        userInvestmentList[msg.sender][id] = UserInvestment({
            pool: pool,
            startTime: block.timestamp,
            endTime: block.timestamp + tiers[tier].locktime,
            tier: tier,
            tokens: tokens,
            amounts: amounts,
            lp: IPool(pool).token()
        });

        // Send token received to pool
    }

    function upgradeTier(
        uint256 id,
        uint256 tier,
        address[] memory tokens,
        uint256[] memory amounts
    ) public {
        // check
        require(
            tier > userInvestmentList[msg.sender][id].tier && tier < 5,
            "LV: Invalid tier"
        );
        uint256 totalUSDAdd = checkPoolAndGetTotalUSDT(
            userInvestmentList[msg.sender][id].pool,
            tokens,
            amounts
        );

        uint totalUSDstaked = getTotalUSD(
            userInvestmentList[msg.sender][id].tokens,
            userInvestmentList[msg.sender][id].amounts
        );

        require(
            totalUSDstaked + totalUSDAdd > tiers[tier].minUSDStake,
            "LV: not enough USD to upgrade"
        );

        // transfer token to user to vault
        for (uint i = 0; i < tokens.length; i++) {
            withdrawFromUser(tokens[i], msg.sender, amounts[i]);
        }

        // transfer token from vault to pool
        uint256 lockedToken = depositVaultToPool(
            userInvestmentList[msg.sender][id].pool,
            userInvestmentList[msg.sender][id].tokens,
            amounts
        );

        userLockedTokens[msg.sender][id] += lockedToken;

        uint256[] memory newAmounts = new uint256[](tokens.length);

        for (uint i = 0; i < tokens.length; i++) {
            newAmounts[i] =
                userInvestmentList[msg.sender][id].amounts[i] +
                amounts[i];
        }

        //upgrade
        userInvestmentList[msg.sender][id] = UserInvestment({
            pool: userInvestmentList[msg.sender][id].pool,
            startTime: block.timestamp,
            endTime: block.timestamp + tiers[tier].locktime,
            tier: tier,
            tokens: tokens,
            amounts: newAmounts,
            lp: userInvestmentList[msg.sender][id].lp
        });
    }

    function claim(uint256 id) public {
        uint256 reward = avaliableUserRewards[msg.sender][id];
        avaliableUserRewards[msg.sender][id] = 0;
        // transfer reward to user
        IERC20(thorn).transfer(msg.sender, reward);
    }

    function withdraw(uint256 id) public {
        uint256 locktime = tiers[userInvestmentList[msg.sender][id].tier]
            .locktime;
        require(
            userInvestmentList[msg.sender][id].startTime + locktime <
                block.timestamp,
            "LV: Not enough time to withdraw"
        );

        // withdraw liquidity from pool to vault
        uint256 lqWithdraw = userLockedTokens[msg.sender][id];
        userLockedTokens[msg.sender][id] = 0;
        uint256[] memory receivedTokens = withdrawFromPoolToVault(
            userInvestmentList[msg.sender][id].pool,
            userInvestmentList[msg.sender][id].tokens,
            lqWithdraw
        );
        // transfer token to user
        for (
            uint i = 0;
            i < userInvestmentList[msg.sender][id].tokens.length;
            i++
        ) {
            IERC20(userInvestmentList[msg.sender][id].tokens[i]).transfer(
                msg.sender,
                receivedTokens[i]
            );
        }

        // transfer reward to user
        uint256 reward = avaliableUserRewards[msg.sender][id] +
            lockedUserRewards[msg.sender][id];
        avaliableUserRewards[msg.sender][id] = 0;
        lockedUserRewards[msg.sender][id] = 0;
        IERC20(thorn).transfer(msg.sender, reward);
    }

    function withdrawPrincipal(uint256 id) public {
        uint256 locktime = tiers[userInvestmentList[msg.sender][id].tier]
            .locktime;
        require(
            userInvestmentList[msg.sender][id].startTime + locktime <
                block.timestamp,
            "LV: Not enough time to withdraw"
        );
        uint256 lqWithdraw = userLockedTokens[msg.sender][id];
        userLockedTokens[msg.sender][id] = 0;
        uint256[] memory receivedTokens = withdrawFromPoolToVault(
            userInvestmentList[msg.sender][id].pool,
            userInvestmentList[msg.sender][id].tokens,
            lqWithdraw
        );
        // transfer token to user
        for (
            uint i = 0;
            i < userInvestmentList[msg.sender][id].tokens.length;
            i++
        ) {
            IERC20(userInvestmentList[msg.sender][id].tokens[i]).transfer(
                msg.sender,
                receivedTokens[i]
            );
        }
    }

    function withdrawProfit(uint256 id) public {
        uint256 locktime = tiers[userInvestmentList[msg.sender][id].tier]
            .locktime;
        require(
            userInvestmentList[msg.sender][id].startTime + locktime <
                block.timestamp,
            "LV: Not enough time to withdraw"
        );
        // transfer reward to user
        uint256 reward = avaliableUserRewards[msg.sender][id] +
            lockedUserRewards[msg.sender][id];
        avaliableUserRewards[msg.sender][id] = 0;
        lockedUserRewards[msg.sender][id] = 0;
        IERC20(thorn).transfer(msg.sender, reward);
    }

    function getUserInvestment(
        address user,
        uint256 id
    ) public view returns (UserInvestment memory) {
        UserInvestment memory result = userInvestmentList[user][id];
        return result;
    }

    function updateRate() public {
        require(
            block.timestamp - lastTimeUpdate > 60,
            "LV: Not enough time to update"
        );
        for (uint i = 1; i < numberUser; i++) {
            address user = numToUser[i];
            for (uint j = 1; j <= userCurrentIds[user]; j++) {
                uint256 locktime = tiers[userInvestmentList[user][j].tier]
                    .locktime;

                if (
                    userInvestmentList[user][j].startTime + locktime >
                    block.timestamp
                ) {
                    uint totalUSD = getTotalUSD(
                        userInvestmentList[user][j].tokens,
                        userInvestmentList[user][j].amounts
                    );

                    uint256 rewardUSD = ((totalUSD *
                        tiers[userInvestmentList[user][j].tier].apr) /
                        100 /
                        60 /
                        60) * (block.timestamp - lastTimeUpdate);

                    uint256 rewardThorn = swapUSDtoThorn(rewardUSD);

                    avaliableUserRewards[user][j] += (rewardThorn * 70) / 100;
                    lockedUserRewards[user][j] += (rewardThorn * 30) / 100;
                }
            }
        }
    }

    // HELPER FUNCTIONS

    function addToAddressList(address user) internal {
        if (userToNum[user] == 0) {
            numberUser += 1;
            userToNum[user] = numberUser;
            numToUser[numberUser] = user;
        }
    }

    function withdrawFromUser(
        address token,
        address sender,
        uint256 amount
    ) internal {
        IERC20(token).transferFrom(sender, address(this), amount);
    }

    function depositVaultToPool(
        address pool,
        address[] memory tokens,
        uint256[] memory amounts
    ) internal returns (uint256) {
