        require(N_COINS == amounts.length, "Invalid number of Amounts");
        if (N_COINS == 2) {
            uint256[2] memory fixedAmounts;
            for (uint256 i = 0; i < 2; i++) {
                fixedAmounts[i] = amounts[i];
                IERC20(tokens[i]).approve(pool, amounts[i]);
            }
            IPool(pool).add_liquidity(fixedAmounts, uint256(0));
        } else {
            uint256[3] memory fixedAmounts;
