                IERC20(tokens[i]).approve(pool, amounts[i]);
            }
            IPool(pool).add_liquidity(fixedAmounts, uint256(0));
        }
        uint256 after_balance = IStableSwapLP(LP).balanceOf(address(this));
        return after_balance - before_balance;
    }

    function withdrawFromPoolToVault(
        address pool,
        address[] memory tokens,
        uint256 amount
    ) internal returns (uint256[] memory tokenAmounts) {
        // get coins number of pool
        uint256 N_COINS = IPool(pool).N_COINS();

        tokenAmounts = new uint256[](N_COINS);
        uint256[] memory before_balances = new uint256[](10);
        uint256[] memory after_balances = new uint256[](10);

        // get token balances of Vault
        for (uint256 i = 0; i < N_COINS; i++) {
            before_balances[i] = IERC20(tokens[i]).balanceOf(address(this));
        }

        if (N_COINS == 2) {
            IPool(pool).remove_liquidity(amount, [uint256(0), uint256(0)]);
        } else {
            IPool(pool).remove_liquidity(
                amount,
                [uint256(0), uint256(0), uint256(0)]
            );
        }

        for (uint256 i = 0; i < N_COINS; i++) {
            after_balances[i] = IERC20(tokens[i]).balanceOf(address(this));
            // calculate the token quantities of the sender
            tokenAmounts[i] = after_balances[i] - before_balances[i];
        }
        return tokenAmounts;
    }

    /**
     * @dev function help get value of Asset , return USD value
     * @param token address of token
     * @param amount amount of token
     */
    function getAssetValue(
        address token,
        uint256 amount
    ) public view returns (uint256) {
        return IPriceToken(priceToken).getAssetValue(token, amount);
    }

    /**
     * @dev function check uniformity of pool token and list token input and return total USDT value
     * @param pool pool address
     * @param tokens list of token address
     * @param amounts list of token amount
     */
    function checkPoolAndGetTotalUSDT(
        address pool,
        address[] memory tokens,
        uint256[] memory amounts
    ) public view returns (uint256) {
        uint256 N_COINS = IPool(pool).N_COINS();

        require(N_COINS == tokens.length, "Invalid number of tokens");
        if (N_COINS == 2) {
            IPoolFactory.StableSwapPairInfo memory info = IPoolFactory(
                poolFactory
            ).getPairInfo(tokens[0], tokens[1]);
            require(pool == info.swapContract, "Invalid Pool");
        } else {
            IPoolFactory.StableSwapThreePoolPairInfo memory info = IPoolFactory(
                poolFactory
            ).getThreePoolPairInfo(tokens[0], tokens[1]);
            require(pool == info.swapContract, "Invalid Pool");
        }
        return getTotalUSD(tokens, amounts);
    }

    function getTotalUSD(
        address[] memory tokens,
        uint256[] memory amounts
    ) public view returns (uint256) {
        uint256 totalUSD = 0;
        for (uint i = 0; i < tokens.length; i++) {
            totalUSD += getAssetValue(tokens[i], amounts[i]);
        }
        return totalUSD;
    }

    function swapUSDtoThorn(
        uint256 amount
    ) public view returns (uint256 amountOut) {
        return IPriceToken(priceToken).USDtoThorn(amount);
    }

    //region ADMIN FUNCTIONS

    function setTier(
        uint256 tier,
        uint256 apr,
        uint256 locktime,
        uint256 minUSDStake
    ) public onlyOwner {
        tiers[tier] = Tier({
            apr: apr,
            locktime: locktime,
            minUSDStake: minUSDStake
        });
    }
}
